/*        Divide the matrix B into four submatrices */

	n1 = f2cmin(*m,*n) / 2;
	n2 = *n - n1;

/*        Factor B11, recursive call */

	claunhr_col_getrfnp2_(&n1, &n1, &a[a_offset], lda, &d__[1], &iinfo);

